var wow = new WOW({
    boxClass: "wow",
    animateClass: "animated",
    offset: 0,
    mobile: !0,
    live: !0,
    callback: function (e) { },
    scrollContainer: null,
    resetAnimation: !0
});
wow.init(),
    $(document).ready(function () {
        $("[data-paroller-factor]").paroller({ type: "background", transition: "all .3s ease-out" }),
            $.sidebarMenu = function (e) {
                var o = ".sidebar-submenu";
                $(e).on("click", "li a", function (e) {
                    var i = $(this),
                        a = i.next();
                    if (a.is(o) && a.is(":visible"))
                        a.slideUp(300, function () {
                            a.removeClass("menu-open")
                        }),
                            a.parent("li").removeClass("active");
                    else if (a.is(o) && !a.is(":visible")) {
                        var t = i
                            .parents("ul")
                            .first();
                        t
                            .find("ul:visible")
                            .slideUp(300)
                            .removeClass("menu-open");
                        var s = i.parent("li");
                        a.slideDown(300, function () {
                            a.addClass("menu-open"),
                                t
                                    .find("li.active")
                                    .removeClass("active"),
                                s.addClass("active")
                        })
                    }
                    a.is(o) && e.preventDefault()
                })
            },
            $.sidebarMenu($(".sidebar-menu"));
        $(function () {
            $(".site-loader").fadeOut(1e3)
        })
    });